import { type } from "os";
import React from "react";
import Swiper, { Pagination, Navigation } from "swiper";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";

const swiper = new Swiper(".swiper-container", {
  // Optional parameters
  direction: "horizontal",
  loop: true,
  slidesPerView: 3,
  spaceBetween: 30,
  centeredSlides: true,
  pagination: {
    el: ".swiper-pagination",
    clickable: true,
  },
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev",
  },
});

type Destination = {
  id: number;
  photo: string;
  location: string;
};
const destinationImages: Destination[] = [
  {
    id: 1,
    photo:
      "https://images.unsplash.com/photo-1530538095376-a4936b35b5f0?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8aXJlbGFuZHxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=400&q=60",
    location: "Ireland",
  },
  {
    id: 1,
    photo:
      "https://images.unsplash.com/photo-1580060839134-75a5edca2e99?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8c291dGglMjBBZnJpY2F8ZW58MHx8MHx8&auto=format&fit=crop&w=400&q=60",
    location: "South Africa",
  },
  {
    id: 1,
    photo:
      "https://images.unsplash.com/photo-1626014903708-4140cb2bf055?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8M3x8d3VoYW58ZW58MHx8MHx8&auto=format&fit=crop&w=400&q=60",
    location: "Ireland",
  },
];

const Suggestions = () => {
  return (
    <>
      <div className="suggestions section">
        <h4 className="suggestion-secondary-text heading">
          Ideas for your next trip
        </h4>
        <h1 className="suggestion-primary-text heading">
          Where are you going next?
        </h1>

        <div className="swiper">
          <div className="swiper-wrapper">
            <div className="swiper-slide">
              <img
                className="destination-img "
                src={destinationImages[0].photo}
                alt=""
              />
              <h4>{destinationImages[0].location}</h4>
            </div>
            <div className="swiper-slide">
              <img
                className="destination-img "
                src={destinationImages[1].photo}
                alt=""
              />
              <h4>{destinationImages[1].location}</h4>
            </div>
            <div className="swiper-slide">
              <img
                className="destination-img "
                src={destinationImages[2].photo}
                alt=""
              />
              <h4>{destinationImages[2].location}</h4>
            </div>
          </div>{" "}
          <div className="swiper-button-next swiperBtn"></div>
          <div className="swiper-button-prev swiperBtn"></div>
          <div className="swiper-pagination">---</div>
        </div>
      </div>
    </>
  );
};

export default Suggestions;
